mapper.select:
select c.id as cid, c.cname, c.cname_en, c.acquisition_method, e.name as element_name, j.name as job_name, c.release_date, r.rerun_start_date, r.rerun_end_date, r.id from rerun r left join characters c on r.c_id = c.id left join element e on c.element_id = e.id left join job j on c.job_id = j.id where 1=1 and c.cname like concat('%',#{keyword, jdbcType=VARCHAR},'%') and r.rerun_start_date = (select max(rerun_start_date) from rerun where rerun.c_id = c.id)
and c.element_id in
#{element}
and c.job_id in
#{job}
and c.acquisition_method in
#{acquisition}
and c.summon_type = 'RR'
and c.delete_flag = false order by c.release_date desc;

select r.* from rerun r left outer join characters c on r.c_id = c.id
c.acquisition_method = 'SUMM' and r.c_id in
#{id}
